function checkSecretMiddleware(req, res, next) {
    if(config.secrets.secret) {
        const secretKey = req.headers[config.secrets.secretHeader];
        if(secretKey !== config.secrets.secret) {
            res.status(400).send();
        }
    }
    next();
}

// Setup base Middlewares
app.use(express.json());
app.use(cors());
if(config.logs.isLogged) {
    app.use(morganLogger('combined', {
        stream: fs.createWriteStream(config.logs.request, {flags: 'a'})
    }));
}
app.use(morganLogger('combined'));
app.use(helmet());
app.use('/api/v1/pdf/create/', (req, res, next) => {
    if(!config.callback.url || !config.callback.protocol) {
        return res.status(400).send();
    }
    req.callback = config.callback;
    next();
})

