app.get('/get-char-codes/', (req, res) => {
    const strCharCodes = {};
    const strLatCharCodes = {};
    const numberCharCodes = {};
    const signCharCodes = {};
    const strUpper = 'АБВГДЕЖЗИЙКЛМНОПРСТУФХЦЧШЩЪЫЬЭЮЯ';
    const strLatUpper = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';
    const numbers = '01234567890';
    const signs = '+-/*';
    const str = strUpper;
    const strLat = strLatUpper;

    str.split('').forEach((char, index) => {
        const charCode = str.charCodeAt(index);
        strCharCodes[charCode] = str[index];
    });

    strLat.split('').forEach((char, index) => {
        const charCode = strLat.charCodeAt(index);
        strLatCharCodes[charCode] = strLat[index];
    });

    numbers.split('').forEach((char, index) => {
        const charCode = numbers.charCodeAt(index);
        numberCharCodes[charCode] = numbers[index];
    });

    signs.split('').forEach((char, index) => {
        const charCode = signs.charCodeAt(index);
        signCharCodes[charCode] = signs[index];
    });
    res.json({
        strCharCodes,
        strLatCharCodes,
        numberCharCodes,
        signCharCodes,
    });
});

interface IServerData {
    uuid: string,
    version: string,
    name: string,
    port: number,
    host: string,
    protocol: string
}

type TServer = http.Server | https.Server;
let server: TServer;
let serverData: IServerData;

// Starting server
if(config.protocol === 'https') {
    const privateKey  = fs.readFileSync(config.ssl.privateKey, 'utf8');
    const certificate = fs.readFileSync(config.ssl.certificate, 'utf8');
    const credentials = {key: privateKey, cert: certificate};

    server = https.createServer(credentials, app);
    serverData = {
        uuid: generateUUID(),
        version,
        name: 'SSL HTTPS server',
        port: config.ports.https,
        host: config.hosts.https,
        protocol: config.protocol,
    }
} else {
    server = http.createServer(app);
    serverData = {
        uuid: generateUUID(),
        version,
        name: 'HTTP server',
        port: config.ports.http,
        host: config.hosts.http,
        protocol: config.protocol,
    }
}

app.get('/', (req, res) => {
    res.json({...serverData, status: 'started'});
});

server.listen({
    port: serverData.port,
    host: serverData.host,
    exclusive: true
}, () => {
    console.log(`${serverData.name} started: ${serverData.host} on port ${serverData.port}`);
});
